import React, { useState } from 'react';
import axios from 'axios';

const BuscaScreen = () => {
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);

  const searchMovies = () => {
    const apiKey = '8ad25d0e';
    axios.get(`http://www.omdbapi.com/?s=${query}&apikey=${apiKey}`)
      .then(response => {
        setMovies(response.data.Search || []);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div style={styles.container}>
      <input
        style={styles.input}
        type="text"
        placeholder="Buscar filmes..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button style={styles.button} onClick={searchMovies}>Buscar</button>
      <ul style={styles.movieList}>
        {movies.map(movie => (
          <li key={movie.imdbID} style={styles.movie}>
            <h2>{movie.Title}</h2>
            {movie.Poster !== 'N/A' && (
              <img src={movie.Poster} alt={`Poster of ${movie.Title}`} style={styles.poster} />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

const styles = {
  container: {
    padding: '16px',
    fontFamily: 'Arial, sans-serif',
  },
  input: {
    height: '40px',
    borderColor: 'gray',
    borderWidth: '1px',
    marginBottom: '16px',
    paddingHorizontal: '8px',
    width: 'calc(100% - 16px)',
  },
  button: {
    height: '40px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    padding: '0 16px',
    cursor: 'pointer',
    marginBottom: '16px',
  },
  movieList: {
    listStyleType: 'none',
    padding: 0,
  },
  movie: {
    padding: '16px',
    borderBottom: '1px solid #ccc',
  },
  poster: {
    width: '100px',
    height: '150px',
    marginTop: '8px',
  },
};

export default BuscaScreen;
